import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import api from '../services/api';
import CommentList from '../components/CommentList';

function PostDetail({ isAuth }) {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');

  useEffect(() => {
    fetchPost();
    fetchComments();
  }, [id]);

  const fetchPost = async () => {
    try {
      const res = await api.get(`/posts/${id}`);
      setPost(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchComments = async () => {
    try {
      const res = await api.get('/comments', { params: { post_id: id } });
      setComments(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleAddComment = async (e) => {
    e.preventDefault();
    try {
      await api.post('/comments', { content: newComment, post_id: id });
      setNewComment('');
      fetchComments();
    } catch (err) {
      alert('Failed to add comment');
    }
  };

  const containerStyle = {
    minHeight: '100vh',
    background: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)',
    padding: '20px',
    position: 'relative',
    overflow: 'hidden'
  };

  const backgroundDecoration = {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    opacity: 0.1,
    zIndex: 0
  };

  const contentStyle = {
    position: 'relative',
    zIndex: 1,
    maxWidth: '800px',
    margin: '0 auto'
  };

  const postContainerStyle = {
    backgroundColor: 'rgba(255, 255, 255, 0.95)',
    padding: '30px',
    borderRadius: '15px',
    marginBottom: '30px',
    boxShadow: '0 8px 16px rgba(0,0,0,0.1)',
    animation: 'slideInLeft 1s ease-out'
  };

  const titleStyle = {
    color: '#333',
    marginBottom: '20px',
    fontSize: '2.2rem',
    textShadow: '1px 1px 2px rgba(0,0,0,0.1)'
  };

  const contentStyleText = {
    color: '#555',
    lineHeight: '1.6',
    fontSize: '1.1rem',
    marginBottom: '20px'
  };

  const authorStyle = {
    color: '#777',
    fontStyle: 'italic',
    marginBottom: '30px'
  };

  const commentsTitleStyle = {
    color: '#fff',
    textAlign: 'center',
    marginBottom: '20px',
    fontSize: '1.8rem',
    textShadow: '2px 2px 4px rgba(0,0,0,0.3)'
  };

  const formStyle = {
    backgroundColor: 'rgba(255, 255, 255, 0.9)',
    padding: '20px',
    borderRadius: '10px',
    marginBottom: '30px',
    boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
    animation: 'fadeInUp 1s ease-out 0.5s both'
  };

  const textareaStyle = {
    width: '100%',
    padding: '12px',
    marginBottom: '15px',
    border: '1px solid #ddd',
    borderRadius: '5px',
    fontSize: '16px',
    minHeight: '80px',
    resize: 'vertical',
    transition: 'border-color 0.3s ease, box-shadow 0.3s ease',
    boxSizing: 'border-box'
  };

  const buttonStyle = {
    backgroundColor: '#ff6b6b',
    color: 'white',
    padding: '12px 24px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
    fontWeight: 'bold',
    transition: 'background-color 0.3s ease, transform 0.3s ease',
    width: '100%'
  };

  const loadingStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '50vh',
    fontSize: '1.5rem',
    color: '#fff',
    animation: 'pulse 2s infinite'
  };

  if (!post) return (
    <div style={containerStyle}>
      <div style={loadingStyle}>Loading...</div>
    </div>
  );

  return (
    <div style={containerStyle}>
      <svg style={backgroundDecoration} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100">
        <defs>
          <pattern id="waves" width="20" height="20" patternUnits="userSpaceOnUse">
            <path d="M0,10 Q5,0 10,10 T20,10 V20 H0 Z" fill="#fff" opacity="0.2"/>
          </pattern>
        </defs>
        <rect width="100%" height="100%" fill="url(#waves)"/>
      </svg>
      <div style={contentStyle}>
        <div style={postContainerStyle}>
          <h1 style={titleStyle}>{post.title}</h1>
          <p style={contentStyleText}>{post.content}</p>
          <p style={authorStyle}>By: {post.author.username}</p>
        </div>

        <h2 style={commentsTitleStyle}>Comments</h2>
        {isAuth && (
          <form onSubmit={handleAddComment} style={formStyle}>
            <textarea
              placeholder="Add a comment"
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              required
              style={textareaStyle}
              onFocus={(e) => {
                e.target.style.borderColor = '#ff6b6b';
                e.target.style.boxShadow = '0 0 0 2px rgba(255, 107, 107, 0.2)';
              }}
              onBlur={(e) => {
                e.target.style.borderColor = '#ddd';
                e.target.style.boxShadow = 'none';
              }}
            />
            <button
              type="submit"
              style={buttonStyle}
              onMouseEnter={(e) => {
                e.target.style.backgroundColor = '#ff5252';
                e.target.style.transform = 'translateY(-2px)';
              }}
              onMouseLeave={(e) => {
                e.target.style.backgroundColor = '#ff6b6b';
                e.target.style.transform = 'translateY(0)';
              }}
            >
              Add Comment
            </button>
          </form>
        )}
        <CommentList comments={comments} isAuth={isAuth} fetchComments={fetchComments} />
      </div>
      <style>
        {`
          @keyframes slideInLeft {
            from {
              opacity: 0;
              transform: translateX(-50px);
            }
            to {
              opacity: 1;
              transform: translateX(0);
            }
          }

          @keyframes fadeInUp {
            from {
              opacity: 0;
              transform: translateY(30px);
            }
            to {
              opacity: 1;
              transform: translateY(0);
            }
          }

          @keyframes pulse {
            0%, 100% {
              opacity: 1;
            }
            50% {
              opacity: 0.5;
            }
          }
        `}
      </style>
    </div>
  );
}

export default PostDetail;
