import React from 'react';
import { Link } from 'react-router-dom';
import api from '../services/api';

function PostCard({ post }) {
  const getInitials = (username) => {
    return username ? username.charAt(0).toUpperCase() : '?';
  };

  const truncateContent = (content, maxLength = 150) => {
    if (!content) return '';
    return content.length > maxLength ? content.substring(0, maxLength) + '...' : content;
  };

  const handleLike = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Please login to like posts');
        return;
      }
      await api.post(`/posts/${post._id}/like`);
      // Refresh the page or update state to reflect the like
      window.location.reload();
    } catch (err) {
      console.error('Failed to like post:', err);
      if (err.response?.status === 401) {
        alert('Please login to like posts');
      } else {
        alert('Failed to like post. Please try again.');
      }
    }
  };

  const isLikedByUser = () => {
    const userId = localStorage.getItem('userId');
    return post.likes?.some(like => like.user === userId);
  };

  return (
    <article className="post-card fade-in-on-scroll">
      <h3 className="post-title">{post.title}</h3>

      <div className="post-author" style={{ marginBottom: '1rem' }}>
        <div className="author-avatar">
          {getInitials(post.author?.username)}
        </div>
        <span>By {post.author?.username || 'Anonymous'}</span>
        <span style={{ marginLeft: 'auto', color: 'rgba(0,0,0,0.5)', fontSize: '0.8rem' }}>
          {new Date(post.createdAt).toLocaleDateString()}
        </span>
      </div>

      {post.content && (
        <p className="post-excerpt">
          {truncateContent(post.content)}
        </p>
      )}

      {/* Tags */}
      {post.tags && post.tags.length > 0 && (
        <div style={{ display: 'flex', gap: '0.5rem', flexWrap: 'wrap', marginBottom: '1rem' }}>
          {post.tags.map((tag, index) => (
            <span key={index} style={{
              background: 'var(--gradient-accent)',
              color: 'white',
              padding: '0.25rem 0.5rem',
              borderRadius: '12px',
              fontSize: '0.75rem',
              fontWeight: '500'
            }}>
              #{tag}
            </span>
          ))}
        </div>
      )}

      {/* Post Actions */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop: '1rem' }}>
        <button
          onClick={handleLike}
          className={`like-btn ${isLikedByUser() ? 'liked' : ''}`}
          style={{
            background: 'none',
            border: 'none',
            color: isLikedByUser() ? '#ff6b6b' : 'rgba(0,0,0,0.6)',
            cursor: 'pointer',
            display: 'flex',
            alignItems: 'center',
            gap: '0.5rem',
            fontSize: '0.9rem',
            fontWeight: '600',
            padding: '0.5rem',
            borderRadius: '20px',
            transition: 'all 0.3s ease'
          }}
        >
          ‚ù§Ô∏è {post.likes?.length || 0}
        </button>

        <Link to={`/posts/${post._id}`} className="read-more-btn">
          üìñ Read More
        </Link>
      </div>
    </article>
  );
}

export default PostCard;
