import React, { useEffect, useState } from 'react';
import api from '../services/api';
import PostCard from '../components/PostCard';

function MyPosts() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  const resolveUserId = () => {
    let userId = localStorage.getItem('userId');
    if (!userId) {
      const token = localStorage.getItem('token');
      if (token) {
        try {
          const payload = JSON.parse(atob(token.split('.')[1]));
          userId = payload?.user?.id;
          if (userId) localStorage.setItem('userId', userId);
        } catch (e) {
          console.log('Failed to decode token for user id');
        }
      }
    }
    return userId;
  };

  const fetchMyPosts = async () => {
    setLoading(true);
    try {
      console.log('[MyPosts] fetching user posts...');
      const userId = resolveUserId();
      if (!userId) {
        console.log('[MyPosts] no userId found; ensure you are logged in');
        setPosts([]);
        setLoading(false);
        return;
      }
      const res = await api.get('/posts', { params: { authorId: userId, t: Date.now() } });
      console.log('[MyPosts] posts fetched:', Array.isArray(res.data) ? res.data.length : res.data);
      setPosts(res.data || []);
    } catch (err) {
      console.error('Error loading my posts:', err);
      setPosts([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
    fetchMyPosts();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <div>
      <section className="hero-section compact">
        <h1 className="hero-title" style={{ marginBottom: '0.5rem' }}>My Posts</h1>
        <p className="hero-subtitle">All posts you have created</p>
      </section>

      {loading ? (
        <div className="loading"><div className="loading-spinner" /></div>
      ) : (
        <div className="posts-grid">
          {posts.length > 0 ? (
            posts.map(post => <PostCard key={post._id} post={post} />)
          ) : (
            <div style={{ textAlign: 'center', padding: '3rem', color: 'rgba(255, 255, 255, 0.7)', fontSize: '1.1rem', gridColumn: '1 / -1' }}>
              You haven't created any posts yet.
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default MyPosts;
