import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import api from '../services/api';
import CommentList from '../components/CommentList';

function PostDetail({ isAuth }) {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');

  useEffect(() => {
    fetchPost();
    fetchComments();
  }, [id]);

  const fetchPost = async () => {
    try {
      const res = await api.get(`/posts/${id}`);
      setPost(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchComments = async () => {
    try {
      const res = await api.get('/comments', { params: { post_id: id } });
      setComments(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const [isLoading, setIsLoading] = useState(false);

  const getInitials = (username) => {
    return username ? username.charAt(0).toUpperCase() : '?';
  };

  const handleAddComment = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      await api.post('/comments', { content: newComment, post_id: id });
      setNewComment('');
      fetchComments();
    } catch (err) {
      alert('Failed to add comment');
    } finally {
      setIsLoading(false);
    }
  };

  if (!post) return (
    <div className="loading">
      <div className="loading-spinner"></div>
    </div>
  );

  return (
    <div>
      {/* Post Content */}
      <article className="post-form" style={{ marginBottom: '3rem' }}>
        <h1 className="post-title" style={{ fontSize: '2.5rem', marginBottom: '1rem' }}>
          {post.title}
        </h1>
        
        <div className="post-author" style={{ marginBottom: '2rem' }}>
          <div className="author-avatar">
            {getInitials(post.author?.username)}
          </div>
          <span>By {post.author?.username || 'Anonymous'}</span>
        </div>
        
        <div style={{ 
          fontSize: '1.1rem', 
          lineHeight: '1.8', 
          color: 'var(--text-primary)',
          whiteSpace: 'pre-wrap'
        }}>
          {post.content}
        </div>
      </article>

      {/* Comments Section */}
      <section>
        <h2 style={{ 
          color: 'white', 
          textAlign: 'center', 
          marginBottom: '2rem',
          fontSize: '2rem',
          fontFamily: 'Playfair Display, serif'
        }}>
          ðŸ’¬ Comments
        </h2>
        
        {isAuth && (
          <div className="post-form">
            <h3 className="form-title">Add Your Comment</h3>
            <form onSubmit={handleAddComment}>
              <div className="form-group">
                <textarea
                  className="form-textarea"
                  placeholder="Share your thoughts..."
                  value={newComment}
                  onChange={(e) => setNewComment(e.target.value)}
                  required
                />
              </div>
              <button 
                type="submit" 
                className="submit-btn btn-hover-effect"
                disabled={isLoading}
              >
                {isLoading ? (
                  <div className="loading-spinner" style={{ width: '20px', height: '20px', margin: '0 auto' }}></div>
                ) : (
                  'ðŸ’­ Post Comment'
                )}
              </button>
            </form>
          </div>
        )}
        
        <CommentList comments={comments} isAuth={isAuth} fetchComments={fetchComments} />
      </section>
    </div>
  );
}

export default PostDetail;
