import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../services/api';
import PostCard from '../components/PostCard';

function Home({ isAuth }) {
  const [posts, setPosts] = useState([]);
  const [newPost, setNewPost] = useState({ title: '', content: '', tags: '' });
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      const res = await api.get('/posts');
      setPosts(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleCreatePost = async (e) => {
    e.preventDefault();
    try {
      await api.post('/posts', newPost);
      setNewPost({ title: '', content: '' });
      fetchPosts();
    } catch (err) {
      alert('Failed to create post');
    }
  };

  return (
    <div>
      {/* Hero Section */}
      <section className="hero-section">
        <h1 className="hero-title">Welcome to BlogSphere</h1>
        <p className="hero-subtitle">
          Discover amazing stories, share your thoughts, and connect with writers from around the world
        </p>
      </section>

      {/* Post Creation Form */}
      {isAuth && (
        <div className="post-form">
          <h2 className="form-title">‚úçÔ∏è Create New Post</h2>
          <form onSubmit={handleCreatePost}>
            <div className="form-group">
              <input
                className="form-input"
                placeholder="What's your story title?"
                value={newPost.title}
                onChange={(e) => setNewPost({ ...newPost, title: e.target.value })}
                required
              />
            </div>
            <div className="form-group">
              <textarea
                className="form-textarea"
                placeholder="Share your thoughts and ideas..."
                value={newPost.content}
                onChange={(e) => setNewPost({ ...newPost, content: e.target.value })}
                required
              />
            </div>
            <button type="submit" className="submit-btn btn-hover-effect">
              üöÄ Publish Post
            </button>
          </form>
        </div>
      )}

      {/* Posts Grid */}
      <div className="posts-grid">
        {posts.length > 0 ? (
          posts.map(post => (
            <PostCard key={post._id} post={post} />
          ))
        ) : (
          <div style={{ 
            textAlign: 'center', 
            padding: '3rem', 
            color: 'rgba(255, 255, 255, 0.7)',
            fontSize: '1.1rem',
            gridColumn: '1 / -1'
          }}>
            üìù No posts yet. {isAuth ? 'Create the first post!' : 'Login to create posts!'}
          </div>
        )}
      </div>
    </div>
  );
}

export default Home;
