import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../services/api';
import PostCard from '../components/PostCard';

function Home({ isAuth }) {
  const [posts, setPosts] = useState([]);
  const [newPost, setNewPost] = useState({ title: '', content: '' });

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      const res = await api.get('/posts');
      setPosts(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleCreatePost = async (e) => {
    e.preventDefault();
    try {
      await api.post('/posts', newPost);
      setNewPost({ title: '', content: '' });
      fetchPosts();
    } catch (err) {
      alert('Failed to create post');
    }
  };

  const containerStyle = {
    minHeight: '100vh',
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    padding: '20px',
    position: 'relative',
    overflow: 'hidden'
  };

  const backgroundPattern = {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    opacity: 0.1,
    zIndex: 0
  };

  const contentStyle = {
    position: 'relative',
    zIndex: 1,
    maxWidth: '800px',
    margin: '0 auto'
  };

  const titleStyle = {
    color: '#fff',
    textAlign: 'center',
    marginBottom: '30px',
    fontSize: '2.5rem',
    textShadow: '2px 2px 4px rgba(0,0,0,0.3)',
    animation: 'fadeInDown 1s ease-out'
  };

  const formStyle = {
    backgroundColor: 'rgba(255, 255, 255, 0.9)',
    padding: '20px',
    borderRadius: '10px',
    marginBottom: '30px',
    boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
    animation: 'fadeInUp 1s ease-out 0.5s both'
  };

  const inputStyle = {
    width: '100%',
    padding: '12px',
    marginBottom: '15px',
    border: '1px solid #ddd',
    borderRadius: '5px',
    fontSize: '16px',
    transition: 'border-color 0.3s ease, box-shadow 0.3s ease',
    boxSizing: 'border-box'
  };

  const textareaStyle = {
    ...inputStyle,
    minHeight: '100px',
    resize: 'vertical'
  };

  const buttonStyle = {
    backgroundColor: '#4CAF50',
    color: 'white',
    padding: '12px 24px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
    fontWeight: 'bold',
    transition: 'background-color 0.3s ease, transform 0.3s ease',
    width: '100%'
  };

  const postsContainerStyle = {
    animation: 'fadeIn 1s ease-out 1s both'
  };

  return (
    <div style={containerStyle}>
      <svg style={backgroundPattern} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100">
        <defs>
          <pattern id="grain" width="100" height="100" patternUnits="userSpaceOnUse">
            <circle cx="25" cy="25" r="1" fill="#fff" opacity="0.3" />
            <circle cx="75" cy="75" r="1" fill="#fff" opacity="0.3" />
            <circle cx="50" cy="10" r="0.5" fill="#fff" opacity="0.2" />
            <circle cx="10" cy="50" r="0.5" fill="#fff" opacity="0.2" />
            <circle cx="90" cy="30" r="0.5" fill="#fff" opacity="0.2" />
          </pattern>
        </defs>
        <rect width="100%" height="100%" fill="url(#grain)" />
      </svg>
      <div style={contentStyle}>
        <h1 style={titleStyle}>Blog Posts</h1>
        {isAuth && (
          <form onSubmit={handleCreatePost} style={formStyle}>
            <input
              placeholder="Title"
              value={newPost.title}
              onChange={(e) => setNewPost({ ...newPost, title: e.target.value })}
              required
              style={inputStyle}
              onFocus={(e) => {
                e.target.style.borderColor = '#4CAF50';
                e.target.style.boxShadow = '0 0 0 2px rgba(76, 175, 80, 0.2)';
              }}
              onBlur={(e) => {
                e.target.style.borderColor = '#ddd';
                e.target.style.boxShadow = 'none';
              }}
            />
            <textarea
              placeholder="Content"
              value={newPost.content}
              onChange={(e) => setNewPost({ ...newPost, content: e.target.value })}
              required
              style={textareaStyle}
              onFocus={(e) => {
                e.target.style.borderColor = '#4CAF50';
                e.target.style.boxShadow = '0 0 0 2px rgba(76, 175, 80, 0.2)';
              }}
              onBlur={(e) => {
                e.target.style.borderColor = '#ddd';
                e.target.style.boxShadow = 'none';
              }}
            />
            <button
              type="submit"
              style={buttonStyle}
              onMouseEnter={(e) => {
                e.target.style.backgroundColor = '#45a049';
                e.target.style.transform = 'translateY(-2px)';
              }}
              onMouseLeave={(e) => {
                e.target.style.backgroundColor = '#4CAF50';
                e.target.style.transform = 'translateY(0)';
              }}
            >
              Create Post
            </button>
          </form>
        )}
        <div style={postsContainerStyle}>
          {posts.map(post => (
            <PostCard key={post._id} post={post} />
          ))}
        </div>
      </div>
      <style>
        {`
          @keyframes fadeInDown {
            from {
              opacity: 0;
              transform: translateY(-30px);
            }
            to {
              opacity: 1;
              transform: translateY(0);
            }
          }

          @keyframes fadeInUp {
            from {
              opacity: 0;
              transform: translateY(30px);
            }
            to {
              opacity: 1;
              transform: translateY(0);
            }
          }

          @keyframes fadeIn {
            from {
              opacity: 0;
            }
            to {
              opacity: 1;
            }
          }
        `}
      </style>
    </div>
  );
}

export default Home;
