import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';
import Login from '../pages/Login';
import Register from '../pages/Register';
import Home from '../pages/Home';
import PostDetail from '../pages/PostDetail';
import '../styles/App.css';
import { initScrollAnimations, initParallaxEffect, scrollToTop, addRippleEffect } from '../utils/scrollAnimations';

function App() {
  const [isAuth, setIsAuth] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuth(true);
    }
  }, []);

  return (
    <Router>
      <AppContent isAuth={isAuth} setIsAuth={setIsAuth} />
    </Router>
  );
}

function AppContent({ isAuth, setIsAuth }) {
  const navigate = useNavigate();
  const [showScrollTop, setShowScrollTop] = useState(false);

  useEffect(() => {
    // Initialize scroll animations
    const observer = initScrollAnimations();
    const cleanupParallax = initParallaxEffect();

    // Handle scroll to top button visibility
    const handleScroll = () => {
      setShowScrollTop(window.pageYOffset > 300);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      observer.disconnect();
      cleanupParallax();
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsAuth(false);
    navigate('/login');
  };

  const handleButtonClick = (e, callback) => {
    addRippleEffect(e);
    if (callback) callback();
  };

  return (
    <div className="app-container">
      {/* Floating Background Elements */}
      <div className="floating-elements">
        <div className="floating-circle"></div>
        <div className="floating-circle"></div>
        <div className="floating-circle"></div>
      </div>

      {/* Navigation */}
      <nav className="navbar">
        <div className="nav-content">
          <Link to="/" className="logo">✨ BlogSphere</Link>
          <div className="nav-links">
            {!isAuth ? (
              <>
                <Link to="/login" className="nav-link">Login</Link>
                <Link to="/register" className="nav-link">Register</Link>
              </>
            ) : (
              <button 
                onClick={(e) => handleButtonClick(e, handleLogout)} 
                className="logout-btn btn-hover-effect"
              >
                Logout
              </button>
            )}
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="main-content">
        <Routes>
          <Route path="/" element={<Home isAuth={isAuth} />} />
          <Route path="/login" element={<Login setIsAuth={setIsAuth} />} />
          <Route path="/register" element={<Register />} />
          <Route path="/posts/:id" element={<PostDetail isAuth={isAuth} />} />
        </Routes>
      </main>

      {/* Scroll to Top Button */}
      <button 
        className={`scroll-to-top ${showScrollTop ? 'visible' : ''}`}
        onClick={(e) => handleButtonClick(e, scrollToTop)}
        aria-label="Scroll to top"
      >
        ↑
      </button>
    </div>
  );
}

export default App;
